import { motion } from "framer-motion";

const Hero = () => {
  return (
    <div 
      className="h-screen flex items-center justify-center text-white relative" 
      style={{ 
        backgroundImage: "url('./background.jpg')",  
        backgroundSize: "cover",
        backgroundPosition: "center"
      }}
    >
<motion.img 
  src="/logo.png" 
  alt="Thrift" 
  className="absolute rounded-full max-w-[120px] max-h-[120px]"
  initial={{ x: "0px" }}                         // Start at the "Be frugal" line
  animate={{ x: "calc(100% - 130px)" }}          // Moves toward the right corner of the mask
  transition={{ duration: 5, ease: "easeInOut" }}
  whileHover={{ rotate: 360, scale: 1.1 }}
  style={{ top: "6px", left: "0px" }}  
/>

      {/* Semi-transparent mask */}
      <div className="absolute w-full bg-white/80 flex items-center justify-center flex-col justify-between p-8 items-start">
        <div className="text-left w-full pl-0">
          <motion.h1 
            className="text-5xl font-bold leading-tight text-black"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Be frugal
          </motion.h1>

          <motion.h2 
            className="text-5xl font-bold leading-tight text-black"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Save resources.
          </motion.h2>

          <motion.h3
            className="text-2xl text-left mt-4 text-gray-800"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Welcome to Thrift 
          </motion.h3>

          <motion.p
            className="text-lg mt-4 text-gray-700"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Being frugal goes beyond saving money—it’s about making thoughtful choices that conserve valuable resources. By reducing waste, conserving water and energy, 
            and opting for sustainable practices, we can minimize our environmental impact. 
            Simple actions like turning off unused lights, fixing leaks, recycling, and using reusable products can significantly reduce resource consumption. Small, 
            consistent efforts today will help preserve the planet for future generations.
          </motion.p>

          <motion.div
            className="flex justify-center gap-6 mt-8 w-full"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.5 }}
          >
            <button className="bg-blue-500 text-white px-6 py-3 rounded-full font-semibold shadow-lg hover:bg-blue-700 transition">
              Get Started
            </button>
            <button className="ml-4 bg-transparent border-2 border-blue-500 text-blue-500 px-6 py-3 rounded-full font-semibold hover:bg-blue-500 hover:text-white transition">
              Learn More
            </button>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
