import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import { AnimatePresence } from "framer-motion";

const Hero = () => {
  const images = [
    "/background1.jpg",
    "/background2.jpg",
    "/background3.jpg",
    "/background4.jpg",
    "/background5.jpg",
    "/background6.jpg"
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 2500);  // Change every 2.5 seconds

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="relative h-screen w-full overflow-hidden">

      {/* Background Slideshow */}
      <AnimatePresence>
        <motion.div
          key={index}
          className="absolute inset-0 bg-cover bg-center"
          style={{ backgroundImage: `url(${images[index]})` }}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 2 }}
        />
      </AnimatePresence>

      {/* Logo */}
      <motion.img 
        src="/logo.png" 
        alt="Thrift" 
        className="absolute top-6 right-9 rounded-full max-w-[120px] max-h-[120px] object-cover"
        initial={{ opacity: 0, scale: 0.5 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1.5, ease: "easeInOut" }}
        whileHover={{ rotate: 360, scale: 1.1 }}
      />

      {/* Full-Width Content with Left Alignment */}
      <div className="absolute inset-0 flex items-center pl-0 w-full">
      <div className="bg-white/90 p-10 shadow-lg w-full">

          {/* Headings */}
          <motion.h1 
            className="text-5xl font-bold leading-tight text-black"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Be frugal
          </motion.h1>

          <motion.h2 
            className="text-5xl font-bold leading-tight text-black"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Save resources.
          </motion.h2>

          <motion.h3
            className="text-2xl mt-4 text-gray-800"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Welcome to Thrift 
          </motion.h3>

          <motion.p
            className="text-lg mt-4 text-gray-700"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Being frugal goes beyond saving money—it’s about making thoughtful choices that conserve valuable resources. By reducing waste, conserving water and energy, 
            and opting for sustainable practices, we can minimize our environmental impact. 
            Simple actions like turning off unused lights, fixing leaks, recycling, and using reusable products can significantly reduce resource consumption. Small, 
            consistent efforts today will help preserve the planet for future generations.
          </motion.p>

          {/* Buttons */}
          <motion.div
            className="flex justify-center gap-6 mt-8"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.5 }}
          >
            <button className="bg-blue-500 text-white px-6 py-3 rounded-full font-semibold shadow-lg hover:bg-blue-700 transition">
              Get Started
            </button>
            <button className="bg-transparent border-2 border-blue-500 text-blue-500 px-6 py-3 rounded-full font-semibold hover:bg-blue-500 hover:text-white transition">
              Learn More
            </button>
          </motion.div>

        </div>
      </div>
    </div>
  );
};

export default Hero;
