import { motion } from "framer-motion";

const Hero = () => {
  return (
    /*<div className="h-screen bg-gradient-to-r from-blue-500 to-purple-600 flex items-center justify-center text-white">*/
    <div 
    className="h-screen flex items-center justify-center text-white" 
    style={{ 
      backgroundImage: "url('/background.jpg')", 
      backgroundSize: "cover", 
      backgroundPosition: "center"
    }}
      >
      <div className="text-center max-w-3xl px-6">
        <motion.h1 
          className="text-6xl font-bold leading-tight"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Welcome to Your Professional Site
        </motion.h1>

        <motion.p
          className="text-lg mt-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1.5 }}
        >
          Create beautiful, responsive, and professional websites with ease. 
          Make a great first impression with a stunning hero section.
        </motion.p>

        <motion.div
          className="mt-8"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.5 }}
        >
          <button className="bg-white text-blue-500 px-6 py-3 rounded-full font-semibold shadow-lg hover:bg-blue-100 transition">
            Get Started
          </button>
          <button className="ml-4 bg-transparent border-2 border-white px-6 py-3 rounded-full font-semibold hover:bg-white hover:text-blue-500 transition">
            Learn More
          </button>
        </motion.div>
      </div>
    </div>
  );
};

export default Hero;
