import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import { AnimatePresence } from "framer-motion";

const Hero = () => {
  const images = [
    "/background1.jpg",
    "/background2.jpg",
    "/background5.jpg",
    "/background7.jpg",
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 2500);  // Change every 2.5 seconds

    return () => clearInterval(interval);
  }, [images.length]);

  // Function to start the Power tool
  const startPower = async () => {
    try {
      await fetch("http://localhost:3001/start-power");  // Trigger backend to start Power
      window.open("http://localhost:5000", "_blank");    // Open Power after starting
    } catch (error) {
      console.error("Failed to start Power:", error);
    }
  };

  return (
    <div className="relative h-screen w-full overflow-hidden">

      {/* Background Slideshow */}
      <AnimatePresence>
        <motion.div
          key={index}
          className="absolute inset-0 bg-cover bg-center"
          style={{ backgroundImage: `url(${images[index]})` }}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 2 }}
        />
      </AnimatePresence>

      {/* Logo */}
      <motion.img 
        src="/logo.png" 
        alt="Thrift" 
        className="absolute top-6 right-9 rounded-full max-w-[120px] max-h-[120px] object-cover"
        initial={{ opacity: 0, scale: 0.5 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1.5, ease: "easeInOut" }}
        whileHover={{ rotate: 360, scale: 1.1 }}
      />

      {/* Content */}
      <div className="absolute inset-0 flex items-center pl-0 w-full">
        <div className="bg-white/90 p-10 shadow-lg w-full">

          {/* Headings */}
          <motion.h1 
            className="text-5xl font-bold leading-tight text-[#3e5868]"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Be frugal
          </motion.h1>

          <motion.h2 
            className="text-5xl font-bold leading-tight text-[#3e5868]"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Save resources.
          </motion.h2>

          <motion.h3
            className="text-2xl mt-4 text-gray-800"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Welcome to Thrift 
          </motion.h3>

          <motion.p
            className="text-lg mt-4 text-gray-700"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Being frugal goes beyond saving money—it’s about making thoughtful choices that conserve valuable resources. 
          </motion.p>

          {/* Buttons */}
          <motion.div
            className="flex justify-center gap-6 mt-8"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.5 }}
          >
            <button 
              onClick={startPower}
              className="bg-[#8ddcde] text-white px-6 py-3 rounded-full font-semibold shadow-lg hover:bg-[#6bbdbf] transition"
            >
              Get Started
            </button>

            <button className="bg-transparent border-2 border-[#3e5868] text-[#3e5868] px-6 py-3 rounded-full font-semibold hover:bg-[#8ddcde] hover:text-white transition">
              Learn More
            </button>
          </motion.div>

        </div>
      </div>
    </div>
  );
};

export default Hero;
