import { motion } from "framer-motion";

const Hero = () => {
  return (
    <div 
      className="h-screen flex items-center justify-center text-white" 
      style={{ 
        backgroundImage: "url('./background.jpg')",  
        backgroundSize: "cover",
        backgroundPosition: "center"
      }}
    >
      {/* Semi-transparent mask */}
      <div className="absolute w-full bg-black/65 p-12 rounded-lg flex items-center justify-center">
        <div className="text-center max-w-3xl px-6">
          <motion.h1 
            className="text-5xl font-bold leading-tight"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Be frugal
          </motion.h1>
          <motion.h2 
            className="text-5xl font-bold leading-tight"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 3 }}
          >
            Save resources.
          </motion.h2>

          <motion.h3
            className="text-2xl mt-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Welcome to Thrift 
          </motion.h3>
          <motion.p
            className="text-2lg mt-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5 }}
          >
            Being frugal goes beyond saving money—it’s about making thoughtful choices that conserve valuable resources. By reducing waste, conserving water and energy, 
            and opting for sustainable practices, we can minimize our environmental impact. 
            Simple actions like turning off unused lights, fixing leaks, recycling, and using reusable products can significantly reduce resource consumption. Small, 
            consistent efforts today will help preserve the planet for future generations
          </motion.p>

          <motion.div
            className="mt-8"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1.5 }}
          >
            <button className="bg-white text-blue-500 px-6 py-3 rounded-full font-semibold shadow-lg hover:bg-blue-100 transition">
              Get Started
            </button>
            <button className="ml-4 bg-transparent border-2 border-white px-6 py-3 rounded-full font-semibold hover:bg-white hover:text-blue-500 transition">
              Learn More
            </button>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
